import API, { graphqlOperation } from '@aws-amplify/api'
import PubSub from '@aws-amplify/pubsub'
import memoize from 'lodash.memoize'

import {
  OnCreateChannelInListSubscription,
  OnUpdateChannelInListSubscription,
} from '../API'

import {
  createChannelList,
  createChannel as createChannelQuery,
} from '../graphql/mutations'
import {
  onCreateChannelInList,
  onUpdateChannelInList,
} from '../graphql/subscriptions'
import { State, ChannelType, List, Listener, MessageType } from '../types'
//@ts-ignore
import config from '../aws-exports.js'
import { getChannelList as getChannelListQuery } from './custom-queries'

API.configure(config)
PubSub.configure(config)

type CustomChannelList = {
  data: {
    getChannelList: {
      id: string
      channels: List<{
        id: string
        name: string
        createdAt: string
        updatedAt: string
        creatorId: string
        messages: List<MessageType>
      }>
    }
  }
}

export const getChannels = memoize(
  async (nextToken = ''): Promise<State['channels']> => {
    let channelList
    try {
      channelList = await (API.graphql(
        graphqlOperation(
          getChannelListQuery({
            channelNextToken: nextToken,
            // ""
          }),
          {
            id: 'global',
          }
        )
      ) as Promise<CustomChannelList>)
    } catch (err) {
      channelList = null
    }

    if (channelList === null || channelList.data.getChannelList === null) {
      try {
        await API.graphql(
          graphqlOperation(createChannelList, { input: { id: 'global' } })
        )
      } catch (err) {
        console.warn('Failed to create channel list ', err)
      }
      return { items: [], nextToken: '' }
    }

    return channelList.data.getChannelList.channels
  },
  // Only memoize when a next token is provided
  n => (n ? n : Date.now())
)

export const createChannel = async (channel: ChannelType) => {
  try {
    const input = {
      ...channel,
      messages: undefined,
      channelChannelListId: 'global',
    }
    await API.graphql(graphqlOperation(createChannelQuery, { input }))
  } catch (err) {
    console.warn('Could not create channel', channel, err)
  }
}

export const onCreateChannel = (channelListId = 'global') => {
  const listener: Listener<OnCreateChannelInListSubscription> = API.graphql(
    graphqlOperation(onCreateChannelInList, {
      channelChannelListId: channelListId,
    })
  )
  return listener
}

export const onUpdateChannel = (channelListId = 'global') => {
  const listener: Listener<OnUpdateChannelInListSubscription> = API.graphql(
    graphqlOperation(onUpdateChannelInList, {
      channelChannelListId: channelListId,
    })
  )
  return listener
}
